(449,'softaculous_pro_setup_info','a:10:{s:4:\"mode\";s:2:\"ai\";s:13:\"business_lang\";s:2:\"en\";s:13:\"business_type\";s:10:\"Technology\";s:20:\"business_description\";a:1:{i:0;s:331:\"We offer a free discovery consultation as an entry point, followed by tiered packages that scale with your needs. For ongoing support, clients can opt into our monthly retainer, which includes DMARC reporting, protocol tuning, and expert troubleshooting. Our  focus is ONLY ON email deliverability and email governance compliance  \";}s:11:\"active_desc\";s:1:\"0\";s:17:\"image_suggestions\";a:6:{i:0;s:20:\"email deliverability\";i:1;s:20:\"consultation service\";i:2;s:15:\"tiered packages\";i:3;s:15:\"DMARC reporting\";i:4;s:16:\"email governance\";i:5;s:22:\"expert troubleshooting\";}s:9:\"theme_pid\";s:32:\"cbc1066587f0e000dbc800dff78025cb\";s:8:\"features\";a:7:{i:0;s:3:\"seo\";i:1;s:11:\"speedycache\";i:2;s:8:\"backuply\";i:3;s:9:\"loginizer\";i:4;s:9:\"pagelayer\";i:5;s:6:\"gosmtp\";i:6;s:11:\"cookieadmin\";}s:15:\"selected_images\";a:21:{i:0;a:2:{s:8:\"image_id\";s:1:\"6\";s:9:\"image_url\";s:70:\"https://www.securiverse.tech/wp-content/uploads/2025/08/what-we-do.png\";}i:1;a:2:{s:8:\"image_id\";s:1:\"7\";s:9:\"image_url\";s:103:\"https://www.securiverse.tech/wp-content/uploads/2025/08/visual-representation-of-DMARC-DKIM-and-SPF.png\";}i:2;a:2:{s:8:\"image_id\";s:1:\"8\";s:9:\"image_url\";s:74:\"https://www.securiverse.tech/wp-content/uploads/2025/08/Heading-narrow.png\";}i:3;a:2:{s:8:\"image_id\";s:1:\"9\";s:9:\"image_url\";s:68:\"https://www.securiverse.tech/wp-content/uploads/2025/08/pACKAGES.png\";}i:4;a:2:{s:8:\"image_id\";s:2:\"10\";s:9:\"image_url\";s:100:\"https://www.securiverse.tech/wp-content/uploads/2025/08/napkin-email-deliverability-holy-trinity.png\";}i:5;a:2:{s:8:\"image_id\";s:2:\"11\";s:9:\"image_url\";s:66:\"https://www.securiverse.tech/wp-content/uploads/2025/08/AUDITS.png\";}i:6;a:2:{s:8:\"image_id\";s:2:\"12\";s:9:\"image_url\";s:80:\"https://www.securiv